mapper.select:
SELECT
	*
FROM t_apacheos 
WHERE 1=1

	AND status = #{status}


	AND typeId = #{typeId}


	AND name like CONCAT('%', #{name}, '%')


	
		ORDER BY ${orderby}
	
	
		ORDER BY createDt DESC
	


	limit #{page},#{limit}

SELECT COUNT(1)
FROM t_apacheos 
WHERE 1=1

	AND status = #{status}


	AND typeId = #{typeId}


	AND name like CONCAT('%', #{name}, '%')
